call å­˜å‚¨è¿‡ç¨‹å (å®å‚)		ä¸ç”¨è§‰å¾—å¾ˆéš¾ï¼Œå…¶å®å°±æ˜¯å°è£…
&lt;/code&gt;&lt;/pre&gt;
&lt;h4 id=&#34;ä¸ƒæ•™ææ¡ˆä¾‹&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#ä¸ƒæ•™ææ¡ˆä¾‹&#34;&gt;#&lt;/a&gt; ä¸ƒã€‚æ•™ææ¡ˆä¾‹&lt;/h4&gt;
&lt;pre&gt;&lt;code class=&#34;language-mariadb&#34;&gt;delimiter $$
create procedure pro_num (OUT ROWS INT)
begin
declare cid INT;
declare found boolean default true;
declare cur_cid cursor for
	select pno form pro;
declare continue handler for not found 
	set found = 0;
set ROWS =0;
open cur_cid;
fetch cur_cid into cid;
while found do
set ROWS=ROWS+1;
fetch cur_cid into cid;
end while;
colse cur_cid
end $$
&lt;/code&gt;&lt;/pre&gt;
